function toggleMenu(){document.getElementById("sideMenu").classList.toggle("active")}document.addEventListener("click",function(e){const t=document.getElementById("sideMenu"),c=document.querySelector(".menu-toggle"),n=t.contains(e.target),s=c.contains(e.target);!n&&!s&&t.classList.contains("active")&&t.classList.remove("active")});function searchArticles(){const e=document.getElementById("searchInput").value.toLowerCase(),t=document.querySelectorAll(".featured-article");t.forEach(t=>{const r=t.querySelector("h2").innerText.toLowerCase(),c=t.querySelector("img").alt.toLowerCase();r.includes(e)||c.includes(e)?t.style.display="block":t.style.display="none"})}document.getElementById("searchInput").addEventListener("keypress",function(e){"Enter"===e.key&&(e.preventDefault(),searchArticles())});
